  //    setoutput(response.data);
  //   }).catch((error)=>{
  //    console.log(error);
  //   });
  //  });

  return (
    <>
    <Nav/>
      <input type="text" placeholder='item' 
      value={item} onChange={e => setItem(e.target.value)} style={{"marginLeft":"50px"}} />
      <input type="number" placeholder='quantity'
      value={quantity} onChange={e => setQuantity(e.target.value)} style={{"marginLeft":"500px","padding":"1px"}}/>
      <input type="number" placeholder='rate'
       value={rate} onChange={e => setRate(e.target.value)} />

      <br/>
      <button onClick={handleSubmit} style={{"marginLeft":"1000px"}}>Total</button>
      <h3>Item List:</h3>
      <p>Name:{itemlist}</p>
      <p>Quantity:{quantitylist}</p>
      <p>Rate:{ratelist}</p>
      <h1>Total: {total}</h1>
