import './App.css';
import { useState,useEffect} from 'react';
import axios from "axios";
import Nav from "./components/nav";

function App() {

  const [ item , setItem ] = useState("");
  const [ quantity , setQuantity ] = useState();
  const [ rate , setRate ] = useState();
  const [ total , setTotal ] = useState(0);
  const[output,setoutput] =useState([]);

  const handleSubmit=()=>{

    const itemDetails={"item":item,"quantity":quantity,"rate":rate,"total":total};

    axios.post("http://localhost:3001/save",itemDetails).then((response)=>{
        console.log(response);
        setItem("");
        setQuantity("");
        setRate("");
        setTotal("");
    }).catch((error)=>{
        console.log(error);
    });  
  };

  useEffect(()=>{

    axios.get("http://localhost:3001/fetch").then((response)=>{
     setoutput(response.data);
    }).catch((error)=>{
     console.log(error);
    });
   });

  return (
    <>
    <Nav/>
      <input type="text" placeholder='item' 
      value={item} onChange={e => setItem(e.target.value)} style={{"marginLeft":"50px"}} />
      <input type="number" placeholder='quantity'
      value={quantity} onChange={e => setQuantity(e.target.value)} style={{"marginLeft":"500px","padding":"1px"}}/>
      <input type="number" placeholder='rate'
       value={rate} onChange={e => setRate(e.target.value)} />

      <br/>
      <button onClick={handleSubmit} style={{"marginLeft":"1000px"}}>Total</button>

<table class="table table-bordered">
<tr>
<th>Item</th>
<th>Rate</th>
<th>Amount</th>
</tr>  

{
  output.map((row, index) => (
    <tr key={index}>
      <td>{row.item}</td>
      <td>{row.quantity}</td>
      <td>{row.rate}</td>
    </tr>
  ))
}


</table>



    </>
  );
}

export default App;
