import './App.css';
import { useState,useEffect } from 'react';
import axios from "axios";
import Nav from "./components/nav"
import Footer from "./components/footer"

function App() {

  const [ item , setItem ] = useState("");
  const [ quantity , setQuantity ] = useState();
  const [ rate , setRate ] = useState();
  const [ total , setTotal ] = useState(0);
  const [ itemlist , setItemList ] = useState();
  const [ quantitylist , setQuantityList ] = useState();
  const [ ratelist , setRateList ] = useState();
  const[output,setoutput] =useState();

  const handleSubmit=()=>{

    const itemDetails={"item":item,"quantity":quantity,"rate":rate,"total":total};

    axios.post("http://localhost:3001/save",itemDetails).then((response)=>{
        console.log(response);
        setItem("");
        setQuantity("");
        setRate("");
        setTotal("");
    }).catch((error)=>{
        console.log(error);
    });  
  };

  const addtotal=()=>{
    setTotal(quantity * rate);
    setItemList(item);
    setQuantityList(quantity);
    setRateList(rate);
  }

  useEffect(()=>{

    axios.post("http://localhost:3001/").then((response)=>{
     setoutput(response.data);
    }).catch((error)=>{
     console.log(error);
    });
   });

  return (
    <>
    <Nav/>
      <input type="text" placeholder='item' 
      value={item} onChange={e => setItem(e.target.value)} style={{"marginLeft":"50px"}} />
      <input type="number" placeholder='quantity'
      value={quantity} onChange={e => setQuantity(e.target.value)} style={{"marginLeft":"500px","padding":"1px"}}/>
      <input type="number" placeholder='rate'
       value={rate} onChange={e => setRate(e.target.value)} />

      <br/>
      <button onClick={addtotal} style={{"marginLeft":"1000px"}}>Total</button>
      <h3>Item List:</h3>
      <p>Name:{itemlist}</p>
      <p>Quantity:{quantitylist}</p>
      <p>Rate:{ratelist}</p>
      <h1>Total: {total}</h1>
      <p>{output}</p>
    <Footer/>
    </>
  );
}

export default App;
