import './App.css';
import { useState,useEffect} from 'react';
import axios from "axios";
import Nav from "./components/nav";

function App() {

  const [ tasktitle , settasktitle ] = useState("");
  const [ taskdescription , settaskdescription ] = useState();
  const[output,setoutput] =useState([]);

  const handleSubmit=()=>{

    const tasktitleDetails={"tasktitle":tasktitle,"taskdescription":taskdescription};

    axios.post("http://localhost:3001/save",tasktitleDetails).then((response)=>{
        console.log(response);
        settasktitle("");
        settaskdescription("");
    }).catch((error)=>{
        console.log(error);
    });  
  };

  useEffect(()=>{

    axios.get("http://localhost:3001/fetch").then((response)=>{
     setoutput(response.data);
    }).catch((error)=>{
     console.log(error);
    });
   });

  return (
    <>
    <Nav/>
      <input type="text" placeholder='tasktitle' 
      value={tasktitle} onChange={e => settasktitle(e.target.value)} style={{"marginLeft":"50px"}} />
      <input type="text" placeholder='taskdescription'
      value={taskdescription} onChange={e => settaskdescription(e.target.value)} style={{"marginLeft":"500px","padding":"1px"}}/> 
      <br/>
      <button onClick={handleSubmit} style={{"marginLeft":"1000px"}}>Total</button>

<table class="table table-bordered">
<tr>
<th>tasktitle</th>
<th>taskdescription</th>
</tr>  

{
  output.map((row, index) => (
    <tr key={index}>
      <td>{row.tasktitle}</td>
      <td>{row.taskdescription}</td>
    </tr>
  ))
}


</table>



    </>
  );
}

export default App;
