import './App.css';
import { useState, useEffect } from 'react';
import axios from "axios";
import Model from "./components/model"; // Correct the import path

function App() {
  const [tasktitle, setTaskTitle] = useState("");
  const [taskdescription, setTaskDescription] = useState("");
  const [tasks, setTasks] = useState([]);
  const [editingTask, setEditingTask] = useState(null);
  const [isModelOpen, setIsModelOpen] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = () => {
    axios.get("http://localhost:3001/fetch")
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleSubmit = () => {
    const taskDetails = {
      tasktitle: tasktitle,
      taskdescription: taskdescription
    };

    axios.post("http://localhost:3001/save", taskDetails)
      .then((response) => {
        console.log(response);
        setTaskTitle("");
        setTaskDescription("");
        fetchTasks(); // Refresh task list
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleEdit = (task) => {
    setEditingTask(task);
    setTaskTitle(task.tasktitle);
    setTaskDescription(task.taskdescription);
    openModel(); // Correct the function name
  };

  const handleUpdate = () => {
    if (!editingTask) return;

    const updatedTask = {
      tasktitle: tasktitle,
      taskdescription: taskdescription
    };

    axios.put(`http://localhost:3001/update/${editingTask._id}`, updatedTask)
      .then((response) => {
        console.log(response);
        setEditingTask(null);
        setTaskTitle("");
        setTaskDescription("");
        fetchTasks(); // Refresh task list
        closeModel(); // Correct the function name
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleDelete = (taskId) => {
    axios.delete(`http://localhost:3001/delete/${taskId}`)
      .then((response) => {
        console.log(response);
        fetchTasks(); // Refresh task list
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // Correct the function name to openModel
  const openModel = () => {
    setIsModelOpen(true);
  };

  // Correct the function name to closeModel
  const closeModel = () => {
    setIsModelOpen(false);
    setEditingTask(null);
    setTaskTitle("");
    setTaskDescription("");
  };

  return (
    <div className="app">
      <header className="navbar">
        <h1>Kanban Board</h1>
      </header>
      <div className="container">
        <div className="task-form">
          <input
            type="text"
            placeholder="Task Title"
            value={tasktitle}
            onChange={(e) => setTaskTitle(e.target.value)}
          />
          <input
            type="text"
            placeholder="Task Description"
            value={taskdescription}
            onChange={(e) => setTaskDescription(e.target.value)}
          />
          {editingTask ? (
            <div>
              <button className="update-button" onClick={handleUpdate}>Update</button>
              <button className="cancel-button" onClick={() => setEditingTask(null)}>Cancel</button>
            </div>
          ) : (
            <button className="add-button" onClick={handleSubmit}>Add Task</button>
          )}
        </div>
        <table className="task-table">
          {/* ... */}
        </table>
        <Model isOpen={isModelOpen} closeModal={closeModel}>
          {/* ... */}
        </Model>
      </div>
    </div>
  );
}

export default App;
