import './App.css';
import { useState, useEffect } from 'react';
import axios from "axios";

function App() {
  const [tasktitle, setTaskTitle] = useState("");
  const [taskdescription, setTaskDescription] = useState("");
  const [output, setOutput] = useState([]);
  const [editingTask, setEditingTask] = useState(null);

  const fetchTasks = () => {
    axios.get("http://localhost:3001/fetch")
      .then((response) => {
        setOutput(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleSubmit = () => {
    const taskDetails = {
      tasktitle: tasktitle,
      taskdescription: taskdescription
    };

    axios.post("http://localhost:3001/save", taskDetails)
      .then((response) => {
        console.log(response);
        setTaskTitle("");
        setTaskDescription("");
        fetchTasks(); // Refresh task list
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleEdit = (task) => {
    setEditingTask(task);
    setTaskTitle(task.tasktitle);
    setTaskDescription(task.taskdescription);
  };

  const handleUpdate = () => {
    if (!editingTask) return;

    const updatedTask = {
      tasktitle: tasktitle,
      taskdescription: taskdescription
    };

    axios.put(`http://localhost:3001/update/${editingTask._id}`, updatedTask)
      .then((response) => {
        console.log(response);
        setEditingTask(null);
        setTaskTitle("");
        setTaskDescription("");
        fetchTasks(); // Refresh task list
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleDelete = (taskId) => {
    axios.delete(`http://localhost:3001/delete/${taskId}`)
      .then((response) => {
        console.log(response);
        fetchTasks(); // Refresh task list
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <input
        type="text"
        placeholder="tasktitle"
        value={tasktitle}
        onChange={(e) => setTaskTitle(e.target.value)}
        style={{ "marginLeft": "50px" }}
      />
      <input
        type="text"
        placeholder="taskdescription"
        value={taskdescription}
        onChange={(e) => setTaskDescription(e.target.value)}
        style={{ "marginLeft": "500px", "padding": "1px" }}
      />
      <br />
      {editingTask ? (
        <>
          <button onClick={handleUpdate} style={{ "marginLeft": "1000px" }}>Update</button>
          <button onClick={() => setEditingTask(null)}>Cancel</button>
        </>
      ) : (
        <button onClick={handleSubmit} style={{ "marginLeft": "1000px" }}>Add Task</button>
      )}

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Task Title</th>
            <th>Task Description</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {output.map((row) => (
            <tr key={row._id}>
              <td>{row.tasktitle}</td>
              <td>{row.taskdescription}</td>
              <td><button onClick={() => handleEdit(row)}>Edit</button></td>
              <td><button onClick={() => handleDelete(row._id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}

export default App;
